settings.custom_css:
selector .elementor-element.elementor-element-e020bf8 .shopengine-cart-table table .shopengine-footer-button {
    width: auto;
    display: inline-block;
}

.shopengine-cart-table .shop_table .product-remove a {
    font-size: 25px !important;
}
selector .shopengine-cart-table table .product-quantity .shopengine-cart-quantity .qty {
    width: 66px;
    outline: 0;
    font-weight: 500;
    border-width: 2px 0 2px 0;
}
selector .shopengine-cart-table table .product-quantity .shopengine-cart-quantity span{
    padding: 0 15px;
    color: #101010;
    font-size: 24px;
    cursor: pointer;
    line-height: 40px;
    outline: 0;
}
selector .shopengine-cart-quantity{
    border-radius: 5px;
    outline: 0;
}
selector .shopengine-cart-table table .product-quantity .shopengine-cart-quantity span {
    font-weight: 500;
    border: 2px solid #F2F2F2;
}
selector .shopengine-cart-table table .product-quantity .shopengine-cart-quantity span.minus-button{
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
}
selector .shopengine-cart-table table .product-quantity .shopengine-cart-quantity span.plus-button {
    border-top-right-radius: 5px;
    border-bottom-right-radius: 5px;
}
selector .shop_table tfoot td{
    border-top: 0
}
selector.elementor-element.elementor-element-e020bf8 .shopengine-cart-table table tbody a {
    font-size: 14px;
    font-weight: 500;
    line-height: 19px;
}

@media screen and (max-width: 992px) {
.shopengine-img-title-wrapper a:last-child {
    padding-left: 15px;
}
selector .shopengine .shopengine-cart-table table .product-name .shopengine-img-title-wrapper {
    display: flex;
    align-items: center;
}
}
@media screen and (max-width: 767px) {
    selector.elementor-element.elementor-element-e020bf8 .shopengine .shopengine-cart-table table .product-name .shopengine-img-title-wrapper {
        align-items: center;
    }
    selector .shopengine-cart-table table.shop_table.shop_table_responsive.cart.woocommerce-cart-form__contents td {
        display: flex;
        align-items: center;
        justify-content: space-between;
    }
    selector .shopengine .shopengine-cart-table table .product-name .shopengine-img-title-wrapper {
        flex-direction: unset;
    }
    selector .shopengine-cart-table table .shopengine-footer-button {
        margin: 5px !important;
    }
    .shopengine-img-title-wrapper a:last-child {
    padding-left: 10px !important;
}
}
@media screen and (max-width: 450px) {
selector .shopengine .shop_table .shopengine-img-title-wrapper a {
    text-align: left;
}
selector.elementor-element.elementor-element-e020bf8 .shopengine-cart-table table.shop_table.shop_table_responsive.cart.woocommerce-cart-form__contents td {
    display: block;
}
selector.elementor-element.elementor-element-e020bf8 .shopengine-cart-table table.shop_table.shop_table_responsive.cart.woocommerce-cart-form__contents td .shopengine-footer-button {
    width: 100%;
    }
   selector .shopengine .shop_table .shopengine-img-title-wrapper {
    padding-left: 20px;
}
    
}

@media screen and (max-width: 400px) {
    selector .shopengine .shopengine-cart-table table .product-name .shopengine-img-title-wrapper {
    display: block;
}
}


/*cart button text uppercase*/
selector .shopengine-cart-table .cart .shopengine-footer-button {
    text-transform: uppercase;
}
/*end cart button text uppercase*/

/*subtola text and value align mobile view*/
@media only screen and (max-width: 768px) {
    selector .shopengine-widget .shopengine-cart-totals .cart_totals .shop_table tr {
        display: flex;
        justify-content: space-between;
    }
}
